import { useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { LogOut, MessageCircle, Send } from "lucide-react";

export default function Profile() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [chatMessage, setChatMessage] = useState("");
  const [chatHistory, setChatHistory] = useState<Array<{ role: 'user' | 'assistant'; message: string }>>([]);

  const chatMutation = useMutation({
    mutationFn: async (message: string) => {
      const response = await apiRequest("/api/ai/chat", "POST", { message });
      return response;
    },
    onSuccess: (data: any) => {
      setChatHistory((prev) => [...prev, { role: 'assistant', message: data.response }]);
      setChatMessage("");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to get AI response. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSendMessage = () => {
    if (!chatMessage.trim()) return;

    setChatHistory((prev) => [...prev, { role: 'user', message: chatMessage }]);
    chatMutation.mutate(chatMessage);
  };

  const handleLogout = () => {
    window.location.href = "/api/logout";
  };

  const initials = user?.firstName && user?.lastName
    ? `${user.firstName[0]}${user.lastName[0]}`
    : user?.email?.[0]?.toUpperCase() || "U";

  return (
    <div className="p-6 space-y-6">
      <div>
        <h1 className="text-4xl font-bold mb-2" data-testid="text-page-title">Profile</h1>
        <p className="text-muted-foreground">Your account and AI assistant</p>
      </div>

      <Card data-testid="card-user-info">
        <CardContent className="p-6">
          <div className="flex items-center gap-4 mb-6">
            <Avatar className="w-20 h-20">
              <AvatarImage src={user?.profileImageUrl || undefined} />
              <AvatarFallback className="text-2xl">{initials}</AvatarFallback>
            </Avatar>
            <div>
              <h2 className="text-2xl font-bold" data-testid="text-user-name">
                {user?.firstName || user?.email || "User"}
              </h2>
              <p className="text-muted-foreground" data-testid="text-user-email">{user?.email}</p>
            </div>
          </div>
          <Button 
            variant="outline" 
            onClick={handleLogout}
            data-testid="button-logout"
          >
            <LogOut className="w-4 h-4 mr-2" />
            Log Out
          </Button>
        </CardContent>
      </Card>

      <Card data-testid="card-ai-assistant">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <MessageCircle className="w-5 h-5 text-primary" />
            Financial Assistant
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <div className="bg-muted/50 rounded-lg p-4 min-h-[300px] max-h-[400px] overflow-y-auto">
              {chatHistory.length === 0 ? (
                <div className="text-center text-muted-foreground py-12">
                  <MessageCircle className="w-12 h-12 mx-auto mb-4 opacity-50" />
                  <p>Ask me anything about personal finance!</p>
                  <p className="text-sm mt-2">Try: "How can I save more money?" or "What's a good emergency fund?"</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {chatHistory.map((chat, index) => (
                    <div
                      key={index}
                      className={`flex ${chat.role === 'user' ? 'justify-end' : 'justify-start'}`}
                      data-testid={`chat-message-${index}`}
                    >
                      <div
                        className={`max-w-[80%] rounded-lg p-3 ${
                          chat.role === 'user'
                            ? 'bg-primary text-primary-foreground'
                            : 'bg-card border'
                        }`}
                      >
                        <p className="text-sm whitespace-pre-wrap">{chat.message}</p>
                      </div>
                    </div>
                  ))}
                  {chatMutation.isPending && (
                    <div className="flex justify-start">
                      <div className="bg-card border rounded-lg p-3">
                        <p className="text-sm text-muted-foreground">Thinking...</p>
                      </div>
                    </div>
                  )}
                </div>
              )}
            </div>
            <div className="flex gap-2">
              <Input
                placeholder="Ask about budgeting, saving, or investments..."
                value={chatMessage}
                onChange={(e) => setChatMessage(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                disabled={chatMutation.isPending}
                data-testid="input-chat-message"
              />
              <Button
                onClick={handleSendMessage}
                disabled={chatMutation.isPending || !chatMessage.trim()}
                data-testid="button-send-message"
              >
                <Send className="w-4 h-4" />
              </Button>
            </div>
            {!process.env.GEMINI_API_KEY && (
              <p className="text-xs text-muted-foreground text-center">
                Note: AI assistant requires Gemini API key to be configured
              </p>
            )}
          </div>
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle>Account Settings</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div>
            <h3 className="font-medium mb-2">Onboarding Status</h3>
            <p className="text-sm text-muted-foreground">
              {user?.onboardingStatus === 'completed' ? 'Completed' : `Step ${user?.onboardingStatus?.replace('step_', '') || '1'}`}
            </p>
          </div>
          <div>
            <h3 className="font-medium mb-2">KYC Status</h3>
            <p className="text-sm text-muted-foreground">
              {user?.kycCompleted ? 'Verified' : 'Pending'}
            </p>
          </div>
          <div>
            <h3 className="font-medium mb-2">Account Aggregator</h3>
            <p className="text-sm text-muted-foreground">
              {user?.aaToken ? 'Connected' : 'Not Connected'}
            </p>
          </div>
          <div>
            <h3 className="font-medium mb-2">UPI Mandate</h3>
            <p className="text-sm text-muted-foreground">
              {user?.mandateId ? 'Approved' : 'Not Set Up'}
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/Onboarding.tsx.content:
import { useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useMutation } from "@tanstack/react-query";
import { Check } from "lucide-react";

export default function Onboarding() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [currentStep, setCurrentStep] = useState(1);
  const [goalName, setGoalName] = useState("");
  const [goalAmount, setGoalAmount] = useState("");

  const updateOnboardingMutation = useMutation({
    mutationFn: async (status: string) => {
      await apiRequest("/api/user/onboarding", "PATCH", { status });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
    },
  });

  const createGoalMutation = useMutation({
    mutationFn: async () => {
      await apiRequest("/api/goals", "POST", {
        name: goalName,
        targetAmount: goalAmount,
        isMain: true,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/goals"] });
    },
  });

  const handleStep1Complete = async () => {
    if (!goalName || !goalAmount) {
      toast({
        title: "Hold up!",
        description: "Please name your goal and set a target amount.",
        variant: "destructive",
      });
      return;
    }

    try {
      await createGoalMutation.mutateAsync();
      await updateOnboardingMutation.mutateAsync("step_2");
      setCurrentStep(2);
      toast({
        title: "Quest Set!",
        description: `Your ${goalName} journey begins now.`,
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to create your goal. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleStep2Complete = async () => {
    await updateOnboardingMutation.mutateAsync("step_3");
    setCurrentStep(3);
    toast({
      title: "Coming Soon!",
      description: "Bank connection will be available soon. Moving to next step.",
    });
  };

  const handleStep3Complete = async () => {
    await updateOnboardingMutation.mutateAsync("step_4");
    setCurrentStep(4);
    toast({
      title: "Almost There!",
      description: "KYC integration coming soon. Moving to final step.",
    });
  };

  const handleStep4Complete = async () => {
    await updateOnboardingMutation.mutateAsync("completed");
    toast({
      title: "Welcome to The Fight!",
      description: "Your financial glow-up starts now!",
    });
    window.location.href = "/";
  };

  const steps = [
    { number: 1, title: "Set Quest", description: "Name your first savings goal" },
    { number: 2, title: "Connect Intel", description: "Link your bank account" },
    { number: 3, title: "Unlock Locker", description: "Complete eKYC" },
    { number: 4, title: "Enable Stash", description: "Approve UPI Mandate" },
  ];

  return (
    <div className="min-h-screen bg-background flex items-center justify-center p-4">
      <div className="w-full max-w-2xl">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold mb-2">Welcome to Bootcamp</h1>
          <p className="text-muted-foreground">Let's get you ready for The Fight</p>
        </div>

        <div className="flex justify-between mb-8">
          {steps.map((step) => (
            <div key={step.number} className="flex flex-col items-center">
              <div
                className={`w-12 h-12 rounded-full flex items-center justify-center font-bold mb-2 ${
                  currentStep > step.number
                    ? "bg-primary text-primary-foreground"
                    : currentStep === step.number
                    ? "bg-primary text-primary-foreground"
                    : "bg-muted text-muted-foreground"
                }`}
                data-testid={`step-indicator-${step.number}`}
              >
                {currentStep > step.number ? <Check className="w-6 h-6" /> : step.number}
              </div>
              <p className="text-xs font-medium text-center">{step.title}</p>
            </div>
          ))}
        </div>

        {currentStep === 1 && (
          <Card data-testid="card-step-1">
            <CardHeader>
              <CardTitle>Set Your Quest</CardTitle>
              <CardDescription>What are you saving for? Give it a name and a target.</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label htmlFor="goal-name">Goal Name</Label>
                <Input
                  id="goal-name"
                  placeholder="e.g., Europe Trip, New Laptop"
                  value={goalName}
                  onChange={(e) => setGoalName(e.target.value)}
                  data-testid="input-goal-name"
                />
              </div>
              <div>
                <Label htmlFor="goal-amount">Target Amount ()</Label>
                <Input
                  id="goal-amount"
                  type="number"
                  placeholder="e.g., 50000"
                  value={goalAmount}
                  onChange={(e) => setGoalAmount(e.target.value)}
                  data-testid="input-goal-amount"
                />
              </div>
              <Button 
                onClick={handleStep1Complete} 
                className="w-full"
                data-testid="button-step-1-continue"
              >
                Continue
              </Button>
            </CardContent>
          </Card>
        )}

        {currentStep === 2 && (
          <Card data-testid="card-step-2">
            <CardHeader>
              <CardTitle>Connect Intel</CardTitle>
              <CardDescription>Link your bank account to track transactions automatically.</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <p className="text-sm text-muted-foreground">
                We use Account Aggregator framework for secure, read-only access to your transactions.
              </p>
              <div className="bg-muted/50 p-4 rounded-lg">
                <p className="text-sm font-medium mb-2">Coming Soon</p>
                <p className="text-sm text-muted-foreground">
                  Bank integration will be available in the next update. For now, you can manually add transactions.
                </p>
              </div>
              <Button 
                onClick={handleStep2Complete} 
                className="w-full"
                data-testid="button-step-2-continue"
              >
                Continue
              </Button>
            </CardContent>
          </Card>
        )}

        {currentStep === 3 && (
          <Card data-testid="card-step-3">
            <CardHeader>
              <CardTitle>Unlock Locker</CardTitle>
              <CardDescription>Complete quick eKYC to create your Liquid Fund account.</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="bg-muted/50 p-4 rounded-lg">
                <p className="text-sm font-medium mb-2">Coming Soon</p>
                <p className="text-sm text-muted-foreground">
                  eKYC integration will be available soon. Your Locker feature is ready to track your savings!
                </p>
              </div>
              <Button 
                onClick={handleStep3Complete} 
                className="w-full"
                data-testid="button-step-3-continue"
              >
                Continue
              </Button>
            </CardContent>
          </Card>
        )}

        {currentStep === 4 && (
          <Card data-testid="card-step-4">
            <CardHeader>
              <CardTitle>Enable Stash</CardTitle>
              <CardDescription>Approve UPI mandate for one-tap savings.</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="bg-muted/50 p-4 rounded-lg">
                <p className="text-sm font-medium mb-2">Coming Soon</p>
                <p className="text-sm text-muted-foreground">
                  UPI AutoPay integration will be available soon. You can still manually track your stash transactions!
                </p>
              </div>
              <Button 
                onClick={handleStep4Complete} 
                className="w-full"
                data-testid="button-step-4-complete"
              >
                Complete Setup
              </Button>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

client/src/pages/Fight.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Sparkles, ThumbsDown, ShoppingBag, Check } from "lucide-react";
import type { Transaction } from "@shared/schema";

export default function Fight() {
  const { toast } = useToast();
  const [currentIndex, setCurrentIndex] = useState(0);

  const { data: transactions = [], isLoading } = useQuery<Transaction[]>({
    queryKey: ["/api/transactions/untagged"],
  });

  const tagMutation = useMutation({
    mutationFn: async ({ id, tag }: { id: string; tag: string }) => {
      await apiRequest(`/api/transactions/${id}/tag`, "PATCH", { tag });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/transactions/untagged"] });
      queryClient.invalidateQueries({ queryKey: ["/api/transactions"] });
      queryClient.invalidateQueries({ queryKey: ["/api/streak"] });
      setCurrentIndex(0);
    },
  });

  const handleTag = async (tag: 'Need' | 'Want' | 'Ick') => {
    const transaction = transactions[currentIndex];
    if (!transaction) return;

    await tagMutation.mutateAsync({ id: transaction.id, tag });

    let message = "";
    if (tag === "Need") {
      message = "Essential purchase tagged!";
    } else if (tag === "Want") {
      message = "Reasonable want noted.";
    } else {
      message = "Ick identified! Fight back by stashing some cash.";
    }

    toast({
      title: "Tagged!",
      description: message,
    });
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <p>Loading transactions...</p>
      </div>
    );
  }

  if (transactions.length === 0) {
    return (
      <div className="p-6">
        <div className="max-w-2xl mx-auto text-center py-12">
          <Check className="w-16 h-16 text-primary mx-auto mb-4" />
          <h2 className="text-2xl font-bold mb-2">All Caught Up!</h2>
          <p className="text-muted-foreground">
            No transactions to categorize right now. Check back later!
          </p>
        </div>
      </div>
    );
  }

  const currentTransaction = transactions[currentIndex];

  return (
    <div className="p-6">
      <div className="max-w-2xl mx-auto">
        <div className="mb-6">
          <h1 className="text-4xl font-bold mb-2" data-testid="text-page-title">The Fight</h1>
          <p className="text-muted-foreground">
            Categorize your spending  {currentIndex + 1} of {transactions.length}
          </p>
        </div>

        <Card data-testid="card-transaction">
          <CardHeader>
            <div className="flex justify-between items-start">
              <div>
                <CardTitle className="text-2xl mb-2" data-testid="text-transaction-description">
                  {currentTransaction.description}
                </CardTitle>
                <p className="text-sm text-muted-foreground">
                  {new Date(currentTransaction.date).toLocaleDateString('en-IN', {
                    day: 'numeric',
                    month: 'long',
                    year: 'numeric'
                  })}
                </p>
              </div>
              <div className="text-right">
                <div className="text-3xl font-bold" data-testid="text-transaction-amount">
                  {parseFloat(currentTransaction.amount).toLocaleString('en-IN')}
                </div>
              </div>
            </div>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-3">
              <Button
                onClick={() => handleTag('Need')}
                variant="outline"
                className="w-full justify-start text-left h-auto py-4"
                disabled={tagMutation.isPending}
                data-testid="button-tag-need"
              >
                <div className="flex items-start gap-3 w-full">
                  <div className="w-10 h-10 bg-primary/10 rounded-lg flex items-center justify-center shrink-0">
                    <Sparkles className="w-5 h-5 text-primary" />
                  </div>
                  <div>
                    <div className="font-semibold mb-1">Need</div>
                    <div className="text-sm text-muted-foreground">
                      Essential expenses like rent, groceries, bills, transportation
                    </div>
                  </div>
                </div>
              </Button>

              <Button
                onClick={() => handleTag('Want')}
                variant="outline"
                className="w-full justify-start text-left h-auto py-4"
                disabled={tagMutation.isPending}
                data-testid="button-tag-want"
              >
                <div className="flex items-start gap-3 w-full">
                  <div className="w-10 h-10 bg-secondary/10 rounded-lg flex items-center justify-center shrink-0">
                    <ShoppingBag className="w-5 h-5 text-secondary" />
                  </div>
                  <div>
                    <div className="font-semibold mb-1">Want</div>
                    <div className="text-sm text-muted-foreground">
                      Non-essential but reasonable like dining out, entertainment
                    </div>
                  </div>
                </div>
              </Button>

              <Button
                onClick={() => handleTag('Ick')}
                variant="outline"
                className="w-full justify-start text-left h-auto py-4"
                disabled={tagMutation.isPending}
                data-testid="button-tag-ick"
              >
                <div className="flex items-start gap-3 w-full">
                  <div className="w-10 h-10 bg-destructive/10 rounded-lg flex items-center justify-center shrink-0">
                    <ThumbsDown className="w-5 h-5 text-destructive" />
                  </div>
                  <div>
                    <div className="font-semibold mb-1">Ick</div>
                    <div className="text-sm text-muted-foreground">
                      Impulse buys, wasteful spending, unnecessary subscriptions
                    </div>
                  </div>
                </div>
              </Button>
            </div>

            {currentTransaction.category && (
              <div className="pt-4 border-t">
                <p className="text-sm text-muted-foreground mb-2">Original Category:</p>
                <Badge variant="secondary">{currentTransaction.category}</Badge>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

