  const achievements = [
    {
      id: "first-stash",
      icon: "",
      title: "First Stash",
      description: "Saved your first 1,000",
      points: 100,
      unlocked: true,
    },
    {
      id: "week-streak",
      icon: "",
      title: "Week Warrior",
      description: "Maintained a 7-day save streak",
      points: 250,
      unlocked: (streak?.saveStreak || 0) >= 7,
    },
    {
      id: "fight-master",
      icon: "",
      title: "Fight Master",
      description: "Categorized 100 transactions",
      points: 300,
      unlocked: false,
    },
    {
      id: "ick-slayer",
      icon: "",
      title: "Ick Slayer",
      description: "Identified 50 Icks",
      points: 200,
      unlocked: false,
    },
    {
      id: "goal-crusher",
      icon: "",
      title: "Goal Crusher",
      description: "Completed your first savings goal",
      points: 500,
      unlocked: false,
    },
    {
      id: "month-master",
      icon: "",
      title: "Month Master",
      description: "Maintained a 30-day save streak",
      points: 1000,
      unlocked: (streak?.saveStreak || 0) >= 30,
    },
    {
      id: "ten-k-club",
      icon: "",
      title: "10K Club",
      description: "Stashed 10,000 total",
      points: 400,
      unlocked: false,
    },
    {
      id: "quest-complete",
      icon: "",
      title: "Knowledge Seeker",
      description: "Completed your first financial literacy quest",
      points: 150,
      unlocked: false,
    },
  ];

  const streakCalendar = Array.from({ length: 30 }, (_, i) => {
    const dayNumber = i + 1;
    const hasActivity = dayNumber <= (streak?.saveStreak || 0);
    return {
      day: dayNumber,
      hasActivity,
    };
  });

  return (
    <div className="p-6 space-y-6">
      <div>
        <h1 className="text-4xl font-bold mb-2" data-testid="text-page-title">Trophy Case</h1>
        <p className="text-muted-foreground">Your badges, streaks, and achievements</p>
      </div>

      <div className="grid md:grid-cols-2 gap-6">
        <Card data-testid="card-save-streak">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Flame className="w-5 h-5 text-destructive" />
              Save Streak
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-center">
              <div className="text-6xl font-bold mb-2" data-testid="text-save-streak">
                {streak?.saveStreak || 0}
              </div>
              <p className="text-muted-foreground">days in a row</p>
            </div>
          </CardContent>
        </Card>

        <Card data-testid="card-fight-streak">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Calendar className="w-5 h-5 text-primary" />
              Fight Streak
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-center">
              <div className="text-6xl font-bold mb-2" data-testid="text-fight-streak">
                {streak?.fightStreak || 0}
              </div>
              <p className="text-muted-foreground">days in a row</p>
            </div>
          </CardContent>
        </Card>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Streak Calendar</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-10 gap-2">
            {streakCalendar.map((day) => (
              <div
                key={day.day}
                className={`aspect-square rounded-md flex items-center justify-center text-xs font-medium ${
                  day.hasActivity
                    ? "bg-primary text-primary-foreground"
                    : "bg-muted text-muted-foreground"
                }`}
                data-testid={`calendar-day-${day.day}`}
              >
                {day.day}
              </div>
            ))}
          </div>
        </CardContent>
      </Card>

      <div>
        <h2 className="text-2xl font-bold mb-4">Achievements</h2>
        <div className="grid md:grid-cols-3 gap-4">
          {achievements.map((achievement) => (
            <Card
              key={achievement.id}
              className={achievement.unlocked ? "" : "opacity-50"}
              data-testid={`card-achievement-${achievement.id}`}
            >
              <CardContent className="p-6">
                <div className="flex items-start gap-4">
                  <div className="text-4xl">{achievement.icon}</div>
                  <div className="flex-1">
                    <h3 className="font-semibold mb-1">{achievement.title}</h3>
                    <p className="text-sm text-muted-foreground mb-2">
                      {achievement.description}
                    </p>
                    <div className="flex items-center gap-2">
                      <Trophy className="w-4 h-4 text-primary" />
                      <span className="text-sm font-medium">{achievement.points} pts</span>
                      {achievement.unlocked && (
                        <span className="text-xs bg-primary/10 text-primary px-2 py-1 rounded ml-auto">
                          Unlocked
                        </span>
                      )}
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    </div>
  );
}

client/src/pages/ExpenseLog.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { ScrollArea } from "@/components/ui/scroll-area";
import { 
  Coffee, Car, ShoppingBag, Ticket, FileText, Tag, 
  Trash2, PlusCircle, Calendar
} from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import ThemeToggle from "@/components/ThemeToggle";
import AddExpenseModal from "@/components/AddExpenseModal";
import { format } from "date-fns";
import type { Transaction } from "@shared/schema";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";

export default function ExpenseLog() {
  const { toast } = useToast();
  const [isAddExpenseOpen, setIsAddExpenseOpen] = useState(false);
  const [deleteId, setDeleteId] = useState<string | null>(null);

  const { data: transactions = [], isLoading, isError } = useQuery<Transaction[]>({
    queryKey: ["/api/transactions"],
    retry: false,
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest(`/api/transactions/${id}`, "DELETE");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/transactions"] });
      queryClient.invalidateQueries({ queryKey: ["/api/transactions/untagged"] });
      toast({
        title: "Deleted",
        description: "Expense deleted successfully",
      });
      setDeleteId(null);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete expense",
        variant: "destructive",
      });
    },
  });

  const getCategoryIcon = (cat: string | null) => {
    const iconClass = "w-5 h-5";
    if (!cat) return <Tag className={iconClass} />;

    switch (cat.toLowerCase()) {
      case 'food': return <Coffee className={iconClass} />;
      case 'transport': return <Car className={iconClass} />;
      case 'shopping': return <ShoppingBag className={iconClass} />;
      case 'entertainment': return <Ticket className={iconClass} />;
      case 'bills': return <FileText className={iconClass} />;
      default: return <Tag className={iconClass} />;
    }
  };

  const getCategoryColor = (cat: string | null) => {
    if (!cat) return 'bg-muted text-muted-foreground';

    switch (cat.toLowerCase()) {
      case 'food': return 'bg-chart-1/10 text-chart-1';
      case 'transport': return 'bg-chart-2/10 text-chart-2';
      case 'shopping': return 'bg-chart-3/10 text-chart-3';
      case 'entertainment': return 'bg-chart-4/10 text-chart-4';
      case 'bills': return 'bg-chart-5/10 text-chart-5';
      default: return 'bg-muted text-muted-foreground';
    }
  };

  const getTagColor = (tag: string | null) => {
    if (!tag) return 'secondary';

    switch (tag) {
      case 'Need': return 'default';
      case 'Want': return 'secondary';
      case 'Ick': return 'destructive';
      default: return 'secondary';
    }
  };

  const formatDate = (date: Date | string) => {
    const d = new Date(date);
    const today = new Date();
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);

    if (d.toDateString() === today.toDateString()) {
      return 'Today';
    } else if (d.toDateString() === yesterday.toDateString()) {
      return 'Yesterday';
    } else {
      return format(d, 'MMM d, yyyy');
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <header className="sticky top-0 z-50 border-b border-primary/20 bg-background/30 backdrop-blur-2xl supports-[backdrop-filter]:bg-background/30 shadow-[0_8px_32px_rgba(139,92,246,0.1)]">
        <div className="container mx-auto px-4 py-3 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <h1 className="text-xl font-bold" data-testid="text-page-title">Expense Log</h1>
            <Badge variant="secondary">{transactions.length} expenses</Badge>
          </div>
          <div className="flex items-center gap-2">
            <Button
              variant="default"
              size="sm"
              onClick={() => setIsAddExpenseOpen(true)}
              data-testid="button-add-expense"
            >
              <PlusCircle className="w-4 h-4 mr-2" />
              Add Expense
            </Button>
            <ThemeToggle />
          </div>
        </div>
      </header>

      <main className="container mx-auto px-4 py-6 max-w-4xl">
        {isLoading ? (
          <Card className="p-8 text-center">
            <p className="text-muted-foreground">Loading expenses...</p>
          </Card>
        ) : isError ? (
          <Card className="p-12 text-center backdrop-blur-xl bg-card/40 border-border/50">
            <h2 className="text-2xl font-bold mb-2">Authentication Required</h2>
            <p className="text-muted-foreground mb-6">
              Please log in to view and manage your expenses
            </p>
            <Button onClick={() => window.location.href = '/api/login'}>
              Log In
            </Button>
          </Card>
        ) : transactions.length === 0 ? (
          <Card className="p-12 text-center backdrop-blur-xl bg-card/40 border-border/50">
            <Calendar className="w-16 h-16 mx-auto mb-4 text-muted-foreground" />
            <h2 className="text-2xl font-bold mb-2">No expenses yet</h2>
            <p className="text-muted-foreground mb-6">
              Start logging your expenses to track your spending
            </p>
            <Button
              onClick={() => setIsAddExpenseOpen(true)}
              data-testid="button-add-first-expense"
            >
              <PlusCircle className="w-4 h-4 mr-2" />
              Add Your First Expense
            </Button>
          </Card>
        ) : (
          <Card className="backdrop-blur-xl bg-card/40 border-border/50">
            <ScrollArea className="h-[calc(100vh-12rem)]">
              <div className="p-4 space-y-2">
                {transactions.map((transaction) => (
                  <div
                    key={transaction.id}
                    className="flex items-center gap-3 p-4 rounded-lg hover-elevate group"
                    data-testid={`item-expense-${transaction.id}`}
                  >
                    <div className={`p-2 rounded-full ${getCategoryColor(transaction.category)}`}>
                      {getCategoryIcon(transaction.category)}
                    </div>

                    <div className="flex-1 min-w-0">
                      <p className="font-semibold text-sm truncate" data-testid={`text-description-${transaction.id}`}>
                        {transaction.description}
                      </p>
                      <div className="flex items-center gap-2 flex-wrap">
                        {transaction.category && (
                          <Badge variant="secondary" className="text-xs" data-testid={`badge-category-${transaction.id}`}>
                            {transaction.category}
                          </Badge>
                        )}
                        {transaction.tag && (
                          <Badge variant={getTagColor(transaction.tag)} className="text-xs" data-testid={`badge-tag-${transaction.id}`}>
                            {transaction.tag}
                          </Badge>
                        )}
                        <span className="text-xs text-muted-foreground" data-testid={`text-date-${transaction.id}`}>
                          {formatDate(transaction.date)}
                        </span>
                      </div>
                    </div>

                    <div className="flex items-center gap-3">
                      <div className="text-right">
                        <p className="font-bold text-base font-mono" data-testid={`text-amount-${transaction.id}`}>
                          {parseFloat(transaction.amount).toFixed(2)}
                        </p>
                      </div>

                      <Button
                        variant="ghost"
                        size="icon"
                        className="opacity-0 group-hover:opacity-100 transition-opacity"
                        onClick={() => setDeleteId(transaction.id)}
                        data-testid={`button-delete-${transaction.id}`}
                      >
                        <Trash2 className="w-4 h-4 text-destructive" />
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
            </ScrollArea>
          </Card>
        )}
      </main>

      <AddExpenseModal
        open={isAddExpenseOpen}
        onOpenChange={setIsAddExpenseOpen}
      />

      <AlertDialog open={deleteId !== null} onOpenChange={(open) => !open && setDeleteId(null)}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Delete Expense?</AlertDialogTitle>
            <AlertDialogDescription>
              This action cannot be undone. This will permanently delete this expense from your log.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel data-testid="button-cancel-delete">Cancel</AlertDialogCancel>
            <AlertDialogAction
              onClick={() => deleteId && deleteMutation.mutate(deleteId)}
              className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
              data-testid="button-confirm-delete"
            >
              Delete
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
}

client/src/pages/AskCoach.tsx.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Send, Sparkles, User } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import ThemeToggle from "@/components/ThemeToggle";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";

interface ChatMessage {
  role: 'user' | 'assistant';
  message: string;
}

export default function AskCoach() {
  const { toast } = useToast();
  const [chatMessage, setChatMessage] = useState("");
  const [chatHistory, setChatHistory] = useState<ChatMessage[]>([
    {
      role: 'assistant',
      message: "Hello! I'm your personal financial coach. I'm here to help you with budgeting, saving strategies, understanding your spending habits, and achieving your financial goals. What would you like to know today?"
    }
  ]);

  const chatMutation = useMutation({
    mutationFn: async (message: string) => {
      const response = await apiRequest("/api/ai/chat", "POST", { message });
      return response;
    },
    onSuccess: (data: any) => {
      setChatHistory((prev) => [...prev, { role: 'assistant', message: data.response }]);
      setChatMessage("");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to get AI response. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSendMessage = () => {
    if (!chatMessage.trim() || chatMutation.isPending) return;

    setChatHistory((prev) => [...prev, { role: 'user', message: chatMessage }]);
    chatMutation.mutate(chatMessage);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const suggestedQuestions = [
    "How can I start saving money?",
    "What's the 50/30/20 budgeting rule?",
    "How do I reduce impulse buying?",
    "Tips for building an emergency fund?"
  ];

  const handleSuggestion = (question: string) => {
    setChatMessage(question);
  };

  return (
    <div className="min-h-screen bg-background">
      <header className="sticky top-0 z-50 border-b border-primary/20 bg-background/30 backdrop-blur-2xl supports-[backdrop-filter]:bg-background/30 shadow-[0_8px_32px_rgba(139,92,246,0.1)]">
        <div className="container mx-auto px-4 py-3 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <h1 className="text-xl font-bold" data-testid="text-page-title">Ask Your Coach</h1>
            <Sparkles className="w-5 h-5 text-primary" />
          </div>
          <ThemeToggle />
        </div>
      </header>

      <main className="container mx-auto px-4 py-6 max-w-4xl">
        <Card className="h-[calc(100vh-12rem)] flex flex-col backdrop-blur-xl bg-card/40 border-border/50">
          <CardHeader className="border-b">
            <CardTitle className="flex items-center gap-2">
              <Sparkles className="w-5 h-5 text-primary" />
              Financial Literacy Coach
            </CardTitle>
            <p className="text-sm text-muted-foreground">
              Your personal AI assistant for financial advice and money management
            </p>
          </CardHeader>

          <ScrollArea className="flex-1 p-4">
            <div className="space-y-4" data-testid="chat-messages">
              {chatHistory.map((msg, index) => (
                <div
                  key={index}
                  className={`flex gap-3 ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}
                  data-testid={`message-${msg.role}-${index}`}
                >
                  {msg.role === 'assistant' && (
                    <Avatar className="w-8 h-8 mt-1">
                      <AvatarFallback className="bg-primary text-primary-foreground">
                        <Sparkles className="w-4 h-4" />
                      </AvatarFallback>
                    </Avatar>
                  )}

                  <div
                    className={`max-w-[80%] rounded-lg p-4 ${
                      msg.role === 'user'
                        ? 'bg-primary text-primary-foreground'
                        : 'bg-muted'
                    }`}
                  >
                    <p className="text-sm whitespace-pre-wrap">{msg.message}</p>
                  </div>

                  {msg.role === 'user' && (
                    <Avatar className="w-8 h-8 mt-1">
                      <AvatarFallback className="bg-secondary">
                        <User className="w-4 h-4" />
                      </AvatarFallback>
                    </Avatar>
                  )}
                </div>
              ))}

              {chatMutation.isPending && (
                <div className="flex gap-3 justify-start">
                  <Avatar className="w-8 h-8 mt-1">
                    <AvatarFallback className="bg-primary text-primary-foreground">
                      <Sparkles className="w-4 h-4" />
                    </AvatarFallback>
                  </Avatar>
                  <div className="bg-muted rounded-lg p-4">
                    <div className="flex gap-1">
                      <div className="w-2 h-2 bg-muted-foreground rounded-full animate-bounce" style={{ animationDelay: '0ms' }} />
                      <div className="w-2 h-2 bg-muted-foreground rounded-full animate-bounce" style={{ animationDelay: '150ms' }} />
                      <div className="w-2 h-2 bg-muted-foreground rounded-full animate-bounce" style={{ animationDelay: '300ms' }} />
                    </div>
                  </div>
                </div>
              )}
            </div>
          </ScrollArea>

          {chatHistory.length === 1 && (
            <CardContent className="border-t border-b bg-muted/20 py-4">
              <p className="text-xs text-muted-foreground mb-2">Suggested questions:</p>
              <div className="grid grid-cols-2 gap-2">
                {suggestedQuestions.map((question, index) => (
                  <Button
                    key={index}
                    variant="outline"
                    size="sm"
                    className="text-xs h-auto py-2 justify-start"
                    onClick={() => handleSuggestion(question)}
                    data-testid={`button-suggestion-${index}`}
                  >
                    {question}
                  </Button>
                ))}
              </div>
            </CardContent>
          )}

          <CardContent className="p-4 border-t">
            <div className="flex gap-2">
              <Input
                value={chatMessage}
                onChange={(e) => setChatMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Ask anything about money, budgeting, or saving..."
                disabled={chatMutation.isPending}
                className="flex-1"
                data-testid="input-chat-message"
              />
              <Button
                onClick={handleSendMessage}
                disabled={!chatMessage.trim() || chatMutation.isPending}
                size="icon"
                data-testid="button-send-message"
              >
                <Send className="w-4 h-4" />
              </Button>
            </div>
          </CardContent>
        </Card>
      </main>
    </div>
  );
}
